import React from 'react';

const Keyboard = ({ guessedLetters, onGuess, restartGame }) => {
  const letters = 'QWERTYUIOPASDFGHJKLZXCVBNM'.split('');

  // Define rows based on a QWERTY layout
  const rows = [
    'QWERTYUIOP',
    'ASDFGHJKL',
    'ZXCVBNM',
  ];

  const handleKeyClick = (letter) => {
    const normalizedLetter = letter.toLowerCase(); // Normalize the letter to lowercase
    if (!guessedLetters.includes(normalizedLetter)) {
      onGuess(normalizedLetter); // Pass the normalized letter to the onGuess function
    }
  };

  return (
    <div className="keyboard">
      {rows.map((row, rowIndex) => (
        <div key={rowIndex} className="keyboard-row">
          {row.split('').map((letter) => {
            const normalizedLetter = letter.toLowerCase(); // Normalize letter to lowercase for comparison
            const isGuessed = guessedLetters.includes(normalizedLetter);
            return (
              <button
                key={normalizedLetter}
                className={`key ${isGuessed ? 'disabled' : ''}`}
                onClick={() => handleKeyClick(letter)}
                disabled={isGuessed} // Disable the key if it's already guessed
              >
                {letter}
              </button>
            );
          })}
        </div>
      ))}
      {/* Reset Button */}
      <div className="keyboard-row row-special">
        <button
          className="key key-reset"
          onClick={restartGame} // Trigger the restartGame function
        >
          Reset
        </button>
      </div>
    </div>
  );
};

export default Keyboard;
