import React from 'react';

const GameBoard = ({ word, guessedLetters }) => {
  const displayWord = word
    .split('')
    .map((char) => (guessedLetters.includes(char) ? char : '_'))
    .join(' ');

  return <div className="game-board">{displayWord}</div>;
};

export default GameBoard;
