import React from 'react';

const GameBoard = ({ word, guessedLetters }) => {
  // Function to render the word with guessed letters visible and others as underscores
  const renderWord = () => {
    return word.split('').map((letter, index) => (
      <span key={index} className="letter">
        {/* Check if the letter has been guessed, display the letter or an underscore */}
        {guessedLetters.includes(letter.toLowerCase()) ? letter : '_'}
      </span>
    ));
  };

  return (
    <div className="game-board">
      {/* Display the word with guessed letters */}
      <div className="word">{renderWord()}</div>
    </div>
  );
};

export default GameBoard;
