import React from 'react';

const HangmanImage = ({ incorrectGuesses }) => {
  // Adjust incorrect guesses to start from state1.GIF
  const adjustedState = Math.min(incorrectGuesses + 1, 11); // Ensure it doesn't exceed max states
  const imagePath = `/hangmandrawings/state${adjustedState}.GIF`;

  return (
    <div
      className="hangman-image"
      style={{
        backgroundImage: `url(${imagePath})`,
        backgroundSize: 'contain', // Ensures the image fits inside the div while maintaining its aspect ratio
        backgroundPosition: 'center', // Centers the background image
        backgroundRepeat: 'no-repeat', // Prevents the image from repeating if smaller than the container
      }}
    />
  );
};

export default HangmanImage;
