  return (
    <div className="game-status">
      {/* Displays the lives remaining if the game is in progress (status 'playing') */}
      {gameStatus === 'playing' && (
        <p>
          Lives Remaining: {maxLives - incorrectGuesses}/{maxLives} {/* Calculates and displays remaining lives */}
        </p>
