import React from 'react';

const HangmanImage = ({ incorrectGuesses }) => {
  // Adjust incorrect guesses to start from state1.GIF
  const adjustedState = Math.min(incorrectGuesses + 1, 11); // Ensure it doesn't exceed max states
  const imagePath = `/hangmandrawings/state${adjustedState}.GIF`;

  return (
    <div className="hangman-image">
      <img src={imagePath} alt={`Hangman stage ${adjustedState}`} />
    </div>
  );
};

export default HangmanImage;
