// Imports React and useEffect hook for side effects
import React, { useEffect } from 'react';

// Defines the Keyboard component, which receives guessedLetters, onGuess, and restartGame as props
const Keyboard = ({ guessedLetters, onGuess, restartGame }) => {
  // Defines an array of uppercase letters for the keyboard
  const letters = 'QWERTYUIOPASDFGHJKLZXCVBNM'.split('');

  // Define rows based on a QWERTY keyboard layout
  const rows = [
    'QWERTYUIOP', // First row of keys
    'ASDFGHJKL',  // Second row of keys
    'ZXCVBNM',    // Third row of keys
  ];

  // Handle key click: Normalizes letter and triggers onGuess if the letter hasn't been guessed
  const handleKeyClick = (letter) => {
    const normalizedLetter = letter.toLowerCase(); // Normalize the letter to lowercase
    if (!guessedLetters.includes(normalizedLetter)) {
      onGuess(normalizedLetter); // Pass the normalized letter to the onGuess function
    }
  };

  // Handle key press event (triggered by physical keypress)
  const handleKeyPress = (event) => {
    const pressedKey = event.key.toUpperCase(); // Convert pressed key to uppercase
    if (letters.includes(pressedKey)) {
      handleKeyClick(pressedKey); // Call handleKeyClick if the key is part of the defined letters
    }
  };

  // Set up and clean up event listener for keypress
  useEffect(() => {
    window.addEventListener('keydown', handleKeyPress); // Add event listener for keypress
    return () => {
      window.removeEventListener('keydown', handleKeyPress); // Clean up event listener on unmount
    };
  }, [guessedLetters]); // Add guessedLetters as dependency to re-run effect when guessedLetters changes

  return (
    <div className="keyboard">
      {/* Map through rows to create the layout of the keyboard */}
      {rows.map((row, rowIndex) => (
        <div key={rowIndex} className="keyboard-row">
          {/* Split each row string into individual letters */}
          {row.split('').map((letter) => {
            const normalizedLetter = letter.toLowerCase(); // Normalize letter to lowercase for comparison
            const isGuessed = guessedLetters.includes(normalizedLetter); // Check if the letter is guessed
            return (
              <button
                key={normalizedLetter} // Use normalized letter as key for uniqueness
                className={`key ${isGuessed ? 'disabled' : ''}`} // Add 'disabled' class if the letter is guessed
                onClick={() => handleKeyClick(letter)} // Handle click event for the key
                disabled={isGuessed} // Disable the button if the letter has been guessed
              >
                {letter} {/* Display the letter on the button */}
              </button>
            );
          })}
        </div>
      ))}

      {/* Special row for the reset button */}
      <div className="keyboard-row row-special">
        <button
          className="key key-reset" // Apply reset key class
          onClick={restartGame} // Trigger the restartGame function when clicked
        >
          Reset {/* Reset button text */}
        </button>
      </div>
    </div>
  );
};

// Exports the Keyboard component for use in other parts of the app
export default Keyboard;
