const Keyboard = ({ guessedLetters, onGuess }) => {
  const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.split('');

  return (
    <div className="keyboard">
      {letters.map((letter) => {
        const isGuessed = guessedLetters.includes(letter.toLowerCase()); // Normalize case
        return (
          <button
            key={letter}
            className={`key ${isGuessed ? 'disabled' : ''}`}
            onClick={() => onGuess(letter)}
            disabled={isGuessed}
          >
            {letter}
          </button>
        );
      })}
    </div>
  );
};
