import React, { useState, useEffect } from 'react';
import GameBoard from './GameBoard';
import Keyboard from './Keyboard';
import HangmanImage from './HangmanImage';
import GameStatus from './GameStatus';
import Help from './Help';
import '/Styles/App.css';

const App = () => {
  const [word, setWord] = useState(''); // Word to guess
  const [guessedLetters, setGuessedLetters] = useState([]); // Letters guessed
  const [incorrectGuesses, setIncorrectGuesses] = useState(0); // Incorrect guesses
  const [gameStatus, setGameStatus] = useState('playing'); // 'playing', 'won', 'lost'
  const [showHelp, setShowHelp] = useState(false);

  const maxLives = 11;

  // Load a random word from dictionary.txt
  useEffect(() => {
    fetch('/dictionary.txt')
      .then((response) => response.text())
      .then((text) => {
        const words = text.split('\n').map((word) => word.trim());
        setWord(words[Math.floor(Math.random() * words.length)]);
      });
  }, []);

  const handleGuess = (letter) => {
    const normalizedLetter = letter.toLowerCase(); // Convert to lowercase

    if (guessedLetters.includes(normalizedLetter) || gameStatus !== 'playing') return;

    const newGuessedLetters = [...guessedLetters, normalizedLetter];
    setGuessedLetters(newGuessedLetters);

    if (!word.toLowerCase().includes(normalizedLetter)) {
      const newIncorrectGuesses = incorrectGuesses + 1;
      setIncorrectGuesses(newIncorrectGuesses);

      if (newIncorrectGuesses === maxLives) {
        setGameStatus('lost');
      }
    } else {
      const allLettersGuessed = word.toLowerCase().split('').every((char) => 
        newGuessedLetters.includes(char)
      );
      if (allLettersGuessed) {
        setGameStatus('won');
      }
    }
  };


  // Restart game
  const restartGame = () => {
    setGuessedLetters([]);
    setIncorrectGuesses(0);
    setGameStatus('playing');
    setShowHelp(false);

    fetch('/dictionary.txt')
      .then((response) => response.text())
      .then((text) => {
        const words = text.split('\n').map((word) => word.trim());
        setWord(words[Math.floor(Math.random() * words.length)]);
      });
  };

  return (
    <div className="app">
      <h1>Hangman</h1>
      <HangmanImage incorrectGuesses={incorrectGuesses} />
      <GameBoard word={word} guessedLetters={guessedLetters} />
      <Keyboard guessedLetters={guessedLetters} onGuess={handleGuess} />
      <GameStatus
        gameStatus={gameStatus}
        restartGame={restartGame}
        incorrectGuesses={incorrectGuesses}
        maxLives={maxLives}
        word={word} // Pass the word to GameStatus
      />
      <button className="help-button" onClick={() => setShowHelp(!showHelp)}>
        {showHelp ? 'Hide Help' : 'Show Help'}
      </button>
      {showHelp && <Help />}
    </div>
  );
};

export default App;
